root_url = "http://localhost:3000"
case "{query}" when 'l' `open #{root_url}` when 'r' `open #{root_url}/rails/routes` when 'm' `open #{root_url}/rails/mailers` when 'q' `open #{root_url}/sidekiq` when 'j' `open http://localhost:8888` when 'e' `open http://localhost:9200/_plugin/kopf` end

